# Combien y a-t-il de list dans cette array ?
def array_length(login)
  return login.length
end

# Quelle est la list la plus courte de cette liste ?
def array_min_length(login)
  login.min {|a, b| a.length <=> b.length}
end
#puts array_min_length(login)

# Combien y-a-t'il de list contenant 5 caractères (le @ ne compte pas pour un caractère)
def count_5_char(login)
  return login.count { |login| login.length == 6 }
end
#puts count_5_char(login)

# Combien commencent par une majuscule (première lettre juste après le @) ?
def xmaj(login)
  return login.count { |list| list[1] =~ /[A-Z]/ }# compare =~ apres le premier caractere,trouve les maj et renvoi un nombre
end
#puts xmaj(login)

# Trie la liste de list par ordre alphabétique.
def sort_login_by_alpha(login)
  return login.sort_by { |list| list[1].downcase }
end

# Trie la liste de list par taille des list (les plus petits en premiers, les plus grands après)
def sort_login_by_size(login)
  return login.sort_by(&:length)
end

# Quelle est la position dans l'array de la personne @epenser ?
def epenser_position(login)
  return login.index('@epenser') + 1
end

# Sors-moi une répartition des list par taille de ces derniers (nombre de list avec 1 caractère, nombre de list avec 2 caractères, nombre de list avec 3 caractères, etc)
def list_repartition(login)
  repartition = Hash.new(0) #je creer le hash

  max_length = (login.max {|a, b| a.length <=> b.length}).length #repartition (spaceship) compare 

  1.upto(max_length) do |length|# boucle jusqua max_lenght
    repartition[length] = login.count { |list| list.length == length }
  end

  return repartition
end

